def maxArea(A):
    maxi = 0
    Ai_max = A[0]
    area = (len(A) - 1)*min(A[-1], Ai_max)
    maxi = max(area, maxi)

    for i in range(0, len(A)):

        if A[i] < Ai_max :
            continue
        else:
            Ai_max = A[i]
            Aj_max = A[-1]
            area = (len(A) - i - 1)*min(Aj_max, A[i])
            maxi = max(area, maxi)

            for j in range(len(A) - 1, i, -1):
                if A[j] <= Aj_max:
                    continue
                else:
                    Aj_max = A[j]
                    area = (j-i)*min(A[j], A[i])
                    maxi = max(area, maxi)
    return maxi

